finalDict = {}

def get_num(strNum):
    pattern = re.compile(u'[\d\.]+')
    getValue = int(float(pattern.match(strNum).group(0)))
    if "万" in strNum:
        getValue = getValue*10000
    return getValue

itemNum = len(itemList)
for rank, item in enumerate(itemList):
    info = item[1]
    name = info[0][0]
    # ---- calculate hot rates, begin ----
    # for example, a post at rank=10, total post number is 200 (likes above 1000), rankValue = square((200-10)/200)
    rankValue = numpy.square((itemNum-rank)/itemNum)
    # ---- calculate hot rates, end ----
    likeAndFavor = get_num(info[1][5])
    fansNum = get_num(info[1][3])
    notesNum = get_num(info[1][1])
    #print([name, rankValue, likeAndFavor, fansNum, notesNum])
    if name not in finalDict:
        finalDict[name] = [[rankValue], likeAndFavor, fansNum, notesNum]
    else:
        finalDict[name][0].append(rankValue)

for key in finalDict:
    averageRankValue = round(sum(finalDict[key][0]),2)
    finalDict[key].append(averageRankValue)
    likesPerPost = round(finalDict[key][1]/finalDict[key][3], 2)
    finalDict[key].append(likesPerPost)

